<?php function generate_terbilang($x){$abil=array("","satu","dua","tiga","empat","lima","enam","tujuh","delapan","sembilan","sepuluh","sebelas");if($x<12)return " ".$abil[$x];elseif($x<20)return generate_terbilang($x-10)." belas";elseif($x<100)return generate_terbilang($x/10)." puluh".generate_terbilang($x%10);elseif($x<200)return " seratus".generate_terbilang($x-100);elseif($x<1000)return generate_terbilang($x/100)." ratus".generate_terbilang($x%100);elseif($x<2000)return " seribu".generate_terbilang($x-1000);elseif($x<1000000)return generate_terbilang($x/1000)." ribu".generate_terbilang($x%1000);elseif($x<1000000000)return generate_terbilang($x/1000000)." juta".generate_terbilang($x%1000000);elseif($x<1000000000000)return generate_terbilang($x/1000000000)." miliar".generate_terbilang(fmod($x,1000000000));elseif($x<1000000000000000)return generate_terbilang($x/1000000000000)." triliun".generate_terbilang(fmod($x,1000000000000));} ?>
