<?php class Paging{function getPosition($row){if(empty($_GET["p"])){$position=0;$_GET["p"]=1;}else $position=($_GET["p"]-1)*$row;return $position;}function countPage($total,$row){$page=ceil($total/$row);return $page;}function navPagination($position,$total){$nav="";$protocol=$_SERVER["HTTPS"]=="on"?"https":"http";$http=$protocol."://".$_SERVER["HTTP_HOST"].$_SERVER["REQUEST_URI"];$r_http=explode("&p",$http);$url=$r_http[0];if($_GET["p"]!=1)$nav.="<li><a href='".$url."&p=1'>← First</a></li>";else $nav.="<li class='prev disabled'><a href='#'>← First</a></li>";for($i=$_GET["p"]-4;$i<=$_GET["p"]+4;$i++){if($i>0&&$i<=$total){if($_GET["p"]==$i)$nav.="<li class='active'><a href='#'>".$i."</a></li>";else $nav.="<li><a href='".$url."&p=".$i."'>".$i."</a></li>";}}if($_GET["p"]<$total)$nav.="<li class='next'><a href='".$url."&p=".$total."'>Last →</a></li>";else $nav.="<li class='next disabled'><a href='#'>Last →</a></li>";return $nav;}} ?>
